import { GeneseMapper } from '..';
describe('GENESE MAPPER arrayMap', function () {
    var gmp = new GeneseMapper(Object);
    // **************************************************************************
    // _areStringOrNumber
    // **************************************************************************
    describe('_areStringOrNumber', function () {
        it('1, undefined => false', function () {
            expect(gmp._areStringOrNumber(1, undefined) === false).toBeTruthy();
        });
        it('undefined, 1 => false', function () {
            expect(gmp._areStringOrNumber(undefined, 1) === false).toBeTruthy();
        });
        it('2, 1 => true', function () {
            expect(gmp._areStringOrNumber(2, 1) === true).toBeTruthy();
        });
        it('2, "1" => true', function () {
            expect(gmp._areStringOrNumber(2, '1') === true).toBeTruthy();
        });
        it('"2", 1 => true', function () {
            expect(gmp._areStringOrNumber('2', 1) === true).toBeTruthy();
        });
        it('"2", "1" => true', function () {
            expect(gmp._areStringOrNumber('2', '1') === true).toBeTruthy();
        });
    });
});
