import { GeneseMapper } from '..';
import { isSameObject } from '../services/tools.service';
describe('GENESE MAPPER _mapIndexableType', function () {
    var gmp = new GeneseMapper(Object);
    // **************************************************************************
    // _mapIndexableType
    // **************************************************************************
    var target = {
        gnIndexableType: {
            country: ''
        }
    };
    var countriesSource = {
        fr: {
            country: 'Allemagne'
        },
        en: {
            country: 'Germany'
        }
    };
    it('undefined, {a: 1} => undefined', function () {
        expect(gmp._mapIndexableType(undefined, { a: 1 }) === undefined).toBeTruthy();
    });
    it('{gnIndexableType: {country: ""}}, countriesSource => {fr: {country: "Allemagne"}}', function () {
        expect(isSameObject(gmp._mapIndexableType(target, countriesSource), countriesSource))
            .toBeTruthy();
    });
});
